(64, 1, '2016-11-13 07:08:47', '2016-11-13 07:08:47', '[vc_row][vc_column][vc_column_text][revpropage_tophero revaturepro_hero_bannerimage=\"236\" revaturepro_tophero_logo=\"239\" revaturepro_tophero_heading=\"We get IT going.\" revaturepro_tophero_description=\"Don’t just move forward in technology—sprint ahead. Our breakthrough training platform, RevaturePro, gives you the advantage, working on the latest leading-edge technologies that put you at the forefront of talent in the tech industry.\" revaturepro_tophero_button1_name=\"CONTACT AN ADVISOR\" revaturepro_tophero_button1_url=\"http://wp.revteks.com/contact-us/\" revaturepro_tophero_button2_name=\"APPLY NOW\" revaturepro_tophero_button2_url=\"#apply\"][/vc_column_text][/vc_column][/vc_row][vc_row][vc_column][sticky_menu_container][sticky_menu_header menu1_text=\"BENEFITS\" menu1_url=\"#benefits\" menu2_text=\"HOW IT WORKS\" menu2_url=\"#howItWorks\" menu3_text=\"ONLINE\" menu3_url=\"#online\" menu4_text=\"CODING BOOTCAMP\" menu4_url=\"#bootcamp\" menu5_text=\"APPLY\" menu5_url=\"#apply\" menu6_text=\"FAQ\" menu6_url=\"#faq\"][/sticky_menu_container][/vc_column][/vc_row][vc_row][vc_column][revpropage_bluebg_moveahead bluebg_title=\"Move Ahead Faster.\" bluebg_description=\"RevaturePro not only gets you up to speed on the programming skills today’s industries require, it puts you miles ahead of the pack.\"][/vc_column][/vc_row][vc_row][vc_column][sticky_blue_steps_container][blue_steps_menu_header blue1_stepsicon=\"252\" blue1_stepsnumber=\"1\" blue1_steps_title=\"Work with relevant technologies\" blue1_steps_description=\"You work intensively with enterprise-level technologies that companies are using today—as well as next-gen and niche technologies that are shaping the future of business.\" blue2_stepsicon=\"260\" blue2_stepsnumber=\"2\" blue2_steps_title=\"Gain real-world experience\" blue2_steps_description=\"You receive valuable industry experience by working on real-world projects so that you’re truly “job-ready.”\" blue3_stepsicon=\"266\" blue3_stepsnumber=\"3\" blue3_steps_title=\"Learn from the best\" blue3_steps_description=\"You’re guided and mentored by highly skilled professionals representing some of the top talent in technology, who not only teach you industry best practices but the “soft skills” that set you up for ultimate success.\" blue4_stepsicon=\"274\" blue4_stepsnumber=\"4\" blue4_steps_title=\"Showcase your talents\" blue4_steps_description=\"You can develop a comprehensive online portfolio that showcases your projects, skills, experience, certifications, academic coursework, and more—equating them to actual industry experience.\" blue5_stepsicon=\"275\" blue5_stepsnumber=\"5\" blue5_steps_title=\"Tailor your program to your needs\" blue5_steps_description=\"You can choose your technology path from a broad range of offerings, as well as learn and work on projects from anywhere.\" blue6_stepsicon=\"276\" blue6_stepsnumber=\"6\" blue6_steps_title=\"Start your technology career faster\" blue6_steps_description=\"Our intensive program gives you the leading-edge, enterprise-level technology skills businesses need in as little as 12 weeks. Best of all, when you successfully complete the program, we’ll hire you as a Revature software development professional!\"][/sticky_blue_steps_container][/vc_column][/vc_row]', 'Revaturepro', '', 'inherit', 'closed', 'closed', '', '5-revision-v1', '', '', '2016-11-13 07:08:47', '2016-11-13 07:08:47', '', 5, 'http://localhost/rough/5-revision-v1/', 0, 'revision', '', 0),
(65, 1, '2016-11-13 07:11:01', '2016-11-13 07:11:01', '[vc_row][vc_column][acadamicpartners title=\"Academic Partnerships\" description=\"Empower your students with enterprise-level skills and real-world training at no additional cost. Join our growing network of partner colleges and universities to stay on the cutting edge.\"][/vc_column][/vc_row][vc_row][vc_column][academicpartners_orange_section description=\"Find out how our program can help your students land at leading Fortune 500 enterprises and government organizations.\" button_title=\"LEARN MORE \" button_url=\"http://wp.revteks.com/contact-us/\"][/vc_column][/vc_row][vc_row][vc_column][left_title_right_image_large left_title=\"Closing the Gap Between College and Career.\" right_image_large=\"60\" left_description=\"\r\n\r\nRevature is the most accessible, affordable, and inclusive talent development solution, providing a highly desirable and diverse talent pool to support the fast-evolving needs of leading companies in the tech sector.\r\n\r\nFor our university partners, Revature offers a no-risk pathway to high-demand careers in technology for all students and graduates. We help bridge the gap by providing in-demand, industry-aligned skills at no cost to you or your students, leading up to a guaranteed job in the tech industry.\r\n\r\nWith Revature’s talent development model, your students gain hands-on experience in enterprise-level, next-gen, and niche technologies. And we’re not just talking about students seeking a master’s in computer science—with our program, hundreds of students with no STEM background whatsoever have gone on to become successful software engineers.\r\n\r\n\"][/vc_column][/vc_row][vc_row][vc_column][revpro_get_hired_title_with_bgimage title=\"\" banner_image=\"68\" description=\"\" banner_section_title=\"Fast-Tracking Careers in Technology.\" banner_section_description=\"We’ll help your students gain the skills, tools, and connections to get not just a job but a fulfilling career. While you help them earn a bachelor’s or master’s degree in computer science or another major, we’ll provide real-world experience in next-gen technologies.\" banner_section_image=\"68\"][revpropage_gethired_list revpro_gethired_title=\"Job-Ready Skills\" revpro_gethired_description=\"\r\n\r\nYour students receive valuable industry experience by working on real-world projects so that they can hit the ground running upon graduation.\r\n\r\n\"][revpropage_gethired_list revpro_gethired_title=\"Guaranteed Hire\" revpro_gethired_description=\"\r\n\r\nRevature hires all graduates who successfully complete our bootcamp—another way we help bridge the gap between college and career.\r\n\r\n\"][revpropage_gethired_list revpro_gethired_title=\"Fortune 500 Enterprises\" revpro_gethired_description=\"Our graduates are employed by highly desirable companies, such as Fortune 500 enterprises, government organizations, and top systems integrators.\"][/vc_column][/vc_row][vc_row][vc_column][home_what_we_do title=\"Become a Partner Today.\" description=\"Let’s discuss how we can help you expand access to high-demand tech careers for all your students. And ask us about priority placement in our regional bootcamps or how to offer a free bootcamp on your campus.\" button_title=\"INQUIRE TODAY\" button_url=\"http://wp.revteks.com/contact-us/\"][/vc_column][/vc_row][vc_row][vc_column][revpro_academic_institutions_bottom_button_txt banner_title=\"RevaturePro for Students\" revpro_bottom_banner_description=\"Learn how you can fast-track your career and work on some of the most innovative, challenging, and rewarding technology for leading companies.\" linktext=\"LEARN MORE\" linkurl=\"http://wp.revteks.com/revaturepro/\" banner_image=\"144\"][/vc_column][/vc_row][vc_row][vc_column][footer_contact_us_orange title=\"Ready to Advance?\" button_title=\"CONTACT US\" button_url=\"http://wp.revteks.com/contact-us/\"][/vc_column][/vc_row]', 'Academic Partners', '', 'inherit', 'closed', 'closed', '',